        uint256 len = musicBytecode.length;
        uint256 mask = 256**(32 - len) - 1;
        assembly {
            let src := add(musicBytecode, 32)
            let dest := add(executorCreation, 68) // 32 + 36 where JUMPSET start (second one)
            for {} gt(len, 31) {
